/* * 文件名：${LogServiceImpl} * 作者：${Tree} * 版本： * 时间：${2014.4.27} * 修改： * 描述：日志  Service实现层 * * * 版权：亚略特 */package com.services.impl;import com.dao.SysLogDao;import com.model.SysLogInfo;import com.vo.PageVO;import com.param.ConfigParam;import com.services.SysLogService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;@Service("logService")public class SysLogServiceImpl implements SysLogService {	@Autowired	private SysLogDao logDao;	@Override	public void saveLog(SysLogInfo log) {		logDao.save(log);	}	@Override	public List<SysLogInfo> getObjListPage(PageVO page, int queryType, Object... values) {		return logDao.getLogPage(page,queryType,values);	}	@Override	public List<SysLogInfo> getObjList(Object... values) {		return logDao.getLogPage(null, ConfigParam.QUERY_TYPE_SOME, values);	}	@Override	public void truncateTable(String tableName) {		logDao.truncateTable(tableName);	}	@Override	public String getTimeArea() {		return logDao.getTimeArea();	}	@Override	public String getTableSize(String tablename) {		return logDao.getTableSize(tablename);	}}